insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('fa1506ed-4868-4b75-bda6-1341df85c822', 'c1cb4718-94af-4017-9671-4561b9738aae', 'Q: What does the Japanese teacher like to do?<br/>I think they like driving a car.', 'Ｑ：日本語の先生は何をするのが好きですか。<br/>Ａ：車を運転するのが好きだと思います。', 'Ｑ：にほんごのせんせいはなにをするのがすきですか。<br/>Ａ：くるまをうんてんするのがすきだとおもいます。', 'Ｑ： 日[に] 本[ほん] 語[ご]の 先[せん] 生[せい]は 何[なに]をするのが 好[す]きですか。<br/>Ａ： 車[くるま]を 運[うん] 転[てん]するのが 好[す]きだと 思[おも]います。', 5, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('fc9b4d11-9820-43e6-ae85-8f1f3c3079d4', 'c1cb4718-94af-4017-9671-4561b9738aae', 'Q: Do you like cleaning your room?<br/>A: No, Don''t like it very much', 'Ｑ：掃除するのが好きですか。<br/>Ａ：いいえ、あまり好きじゃないです。', 'Ｑ：そうじするのがすきですか。<br/>Ａ：いいえ、あまりすきじゃないです。', 'Ｑ： 掃[そう] 除[じ]するのが 好[す]きですか。<br/>Ａ：いいえ、あまり 好[す]きじゃないです。', 6, cast('{}' as varchar[]));
