insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('002822fc-a376-443e-8ca6-3ebb219fc971', 'f7851ce8-e9b1-47c4-82bc-47626526312a', 'Kento asked something, but I did not understand.', 'けんとさんは何か聞きましたが、分かりませんでした。', 'けんとさんはなにかききましたが、わかりませんでした。', 'けんとさんは 何[なに]か 聞[き]きましたが、 分[わ]かりませんでした。', 5, cast('{}' as varchar[]));
