insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('a5041ba3-065a-4d66-811e-36f31c9c6b13', 'ef61ea7d-f297-402a-8d20-ec499c26e9a2', 'A: Hey, my pork cutlet is gone!<br/>B: I think Tom ate it.', 'Ａ：あ、私のとんかつがない！<br/>Ｂ：トムさんが食べたと思います。', 'Ａ：あ、わたしのとんかつがない！<br/>Ｂ：トムさんがたべたとおもいます。', 'Ａ：あ、 私[わたし]のとんかつがない！<br/>Ｂ：トムさんが 食[た]べたと 思[おも]います。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('bf314136-c7ba-46b5-bb9f-5965d4e87342', 'ef61ea7d-f297-402a-8d20-ec499c26e9a2', 'I don''t think last winter was very cold.<br/>(lit., I think last winter was not very cold.)', '去年の冬はあまり寒くなかったと思います。', 'きょねんのふゆはあまりさむくなかったとおもいます。', '去[きょ] 年[ねん]の 冬[ふゆ]はあまり 寒[さむ]くなかったと 思[おも]います。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('41df0653-bf1b-4ed3-9e0a-88af433150b8', 'ef61ea7d-f297-402a-8d20-ec499c26e9a2', 'I think Tadashi''s father was good-looking when he was young.', 'ただしさんのお父さんは若い時、かっこよかったと思います。', 'ただしさんのおとうさんはわかいとき、かっこよかったとおもいます。', 'ただしさんのお 父[とう]さんは 若[わか]い 時[とき]、かっこよかったと 思[おも]います。', 3, cast('{}' as varchar[]));
