insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('27c0049d-2162-480b-ab6a-fda7bc444d0e', 'a8af87a3-4897-49b5-941b-e608727e4751', 'Yasmin said that she didn''t have lunch.', 'ヤスミンさんは昼ご飯を食べなかったと言っていました。', 'ヤスミンさんはひるごはんをたべなかったといっていました。', 'ヤスミンさんは 昼[ひる]ご 飯[はん]を 食[た]べなかったと 言[い]っていました。', 1, cast('{}' as varchar[]));
