insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('235f3dcf-ce1d-4b55-b3ee-5c4c3120ccdf', 'a8af87a3-4897-49b5-941b-e608727e4751', 'Yasmin said she was sick.', 'ヤスミンさんは病気だったと言っていました。', 'ヤスミンさんはびょうきだったといっていました。', 'ヤスミンさんは 病[びょう] 気[き]だったと 言[い]っていました。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('41a06609-1d7d-410d-917c-64e8ab560392', 'a8af87a3-4897-49b5-941b-e608727e4751', 'Takeshi said that kabuki started at twelve o''clock, and finished at four o''clock.', 'たけしさんは、歌舞伎は十二時に始まって、四時に終わったと言っていました。', 'たけしさんは、かぶきはじゅうにじにはじまって、よじにおわったといっていました。', 'たけしさんは、 歌[か] 舞[ぶ] 伎[き]は 十[じゅう] 二[に] 時[じ]に 始[はじ]まって、 四[よ] 時[じ]に 終[お]わったと 言[い]っていました。', 4, cast('{}' as varchar[]));
