insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('7ed6ed63-8c75-446c-ab22-41946ead4d74', '0653c4b2-5685-4913-ac34-24977b7c60dd', 'I have already done the homework', 'もう宿題をしました。', 'もうしゅくだいをしました。', 'もう 宿[しゅく] 題[だい]をしました。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('570e9173-ed24-4d8a-af37-9a9b8224ed60', '0653c4b2-5685-4913-ac34-24977b7c60dd', 'I have not done the homework yet.', 'まだ宿題をしていません。', 'まだしゅくだいをしていません。', 'まだ 宿[しゅく] 題[だい]をしていません。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('12427994-7440-40b6-996e-26c982b5f8d7', '0653c4b2-5685-4913-ac34-24977b7c60dd', 'Q: Did you already memorize the new kanji?<br/>A: Yes, I already memorized them.', 'Ｑ：もう新しい漢字を覚えましたか。<br/>Ａ：はい、もう覚えました。', 'Ｑ：もうあたらしいかんじをおぼえましたか。<br/>Ａ：はい、もうおぼえました。', 'Ｑ：もう 新[あたら]しい 漢[かん] 字[じ]を 覚[おぼ]えましたか。<br/>Ａ：はい、もう 覚[おぼ]えました。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('e6717229-68cd-48bf-94ba-31ebfeeba666', '0653c4b2-5685-4913-ac34-24977b7c60dd', 'Q: Did you already talk with the new teacher?<br/>A: No, I have not yet talked to them.', 'Ｑ：もう新しい先生と話しましたか。<br/>Ａ：いいえ、まだ話していません。', 'Ｑ：もうあたらしいせんせいとはなしましたか。<br/>Ａ：いいえ、まだはなしていません。', 'Ｑ：もう 新[あたら]しい 先[せん] 生[せい]と 話[はな]しましたか。<br/>Ａ：いいえ、まだ 話[はな]していません。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('6f28b5bb-a759-484e-bfc2-337819e68267', '0653c4b2-5685-4913-ac34-24977b7c60dd', 'Q: Did you already memorize your lesson 10 vocabulary?<br/>A: No, I haven''t memorized them yet.', 'Ｑ：もう十課の単語を覚えた？<br/>Ａ：ううん、まだ覚えていない。', 'Ｑ：もうじゅうかのたんごをおぼえた？<br/>Ａ：ううん、まだおぼえていない。', 'Ｑ：もう 十[じゅう] 課[か]の 単[たん] 語[ご]を 覚[おぼ]えた？<br/>Ａ：ううん、まだ 覚[おぼ]えていない。', 5, cast('{}' as varchar[]));
