insert into "grammar"."rule" ("id", "meaning", "title_preferred_spelling", "title_phonetic_spelling", "title_furigana_template", "how_to_use", "jlpt_level", "row_num", "date_added", "tags") values ('032d9866-ff59-4863-9b00-b8c4b5adc110', 'S, because E.<br/>E; therefore S.', 'Ｅから、Ｓ。', null, null, cast('{"(explanation)から、(situation)。","(explanation formal verb)から、(situation)。 ⟶ usually in suggestions","(explanation formal verb)から、(situation)か。 ⟶ usually in requests"}' as text[]), null, 39, date '2023-10-02', cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('2438c90e-820e-4683-accb-d6b0ef3cda4d', '032d9866-ff59-4863-9b00-b8c4b5adc110', 'I will study this evening, because we will have an exam tomorrow.<br/>We will have an exam tomorrow; therefore, I will study this evening.', '明日試験があるから、今晩勉強します。', 'あしたしけんがあるから、こんばんべんきょうします。', '明[あ] 日[した] 試[し] 験[けん]があるから、 今[こん] 晩[ばん] 勉[べん] 強[きょう]します。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('753049ed-6851-4499-8f97-10b29c1ee2a8', '032d9866-ff59-4863-9b00-b8c4b5adc110', 'We didn''t go out, because it was cold.<br/>It was cold; therefore we didn''t go out.', '寒かったから、出かけませんでした。', 'さむかったから、でかけませんでした。', '寒[さむ]かったから、 出[で]かけませんでした。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('eba0db7d-5b0d-4824-b049-0631047d2253', '032d9866-ff59-4863-9b00-b8c4b5adc110', 'I have kabuki tickets; (therefore) let''s go see it together.', '歌舞伎のチケットがありますから、一緒に見に行きましょう。', 'かぶきのチケットがありますから、いっしょにみにいきましょう。', '歌[か] 舞[ぶ] 伎[き]のチケットがありますから、 一[いっ] 緒[しょ]に 見[み]に 行[い]きましょう。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('408caa1e-7bce-4887-a902-059cba7004be', '032d9866-ff59-4863-9b00-b8c4b5adc110', 'I got two concert tickets, (therefore) would you like to go?', 'コンサートのチケットを二枚もらったから、行きませんか。', 'コンサートのチケットをにまいもらったから、いきませんか。', 'コンサートのチケットを 二[に] 枚[まい]もらったから、 行[い]きませんか。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('baea1be8-e2ec-43ca-a845-9096471fb9be', '032d9866-ff59-4863-9b00-b8c4b5adc110', 'I was very lonely because I did not have any friends.', '友達がいなかったから、とても寂しかったです。', 'ともだちがいなかったから、とてもさびしかったです。', '友[とも] 達[だち]がいなかったから、とても 寂[さび]しかったです。', 5, cast('{}' as varchar[]));
