insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('6c552bbe-183c-4df4-81a9-a9f0c6a90168', 'a1ddb025-9616-4444-a349-8e65c522e55c', 'China is larger than Japan.', '中国の方が日本より大きいです。', 'ちゅうごくのほうがにほんよりおおきいです。', '中[ちゅう] 国[ごく]の 方[ほう]が 日[に] 本[ほん]より 大[おお]きいです。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('7413e5d5-8e3f-498d-ac02-19ec854b8390', 'a1ddb025-9616-4444-a349-8e65c522e55c', 'Sundays are more fun than Mondays.', '日曜日の方が月曜日より楽しいです。', 'にちようびのほうがげつようびよりたのしいです。', '日[にち] 曜[よう] 日[び]の 方[ほう]が 月[げつ] 曜[よう] 日[び]より 楽[たの]しいです。', 2, cast('{}' as varchar[]));
