insert into "grammar"."rule" ("id", "meaning", "title_preferred_spelling", "title_phonetic_spelling", "title_furigana_template", "how_to_use", "jlpt_level", "row_num", "date_added", "tags") values ('e2524a54-3d54-4645-8a43-303d5bff59cb', 'Between A and B, which his more ____?', 'ＡとＢとどちらの方が〇〇か。', null, null, cast('{"noun + と + noun + と + どちら + の方 + が + property + か","noun + と + noun + と + どっち + の方 + が + property + ？","noun + と + noun + (と) + どちら + が + property + か","noun + と + noun + (と) + どっち + が + property + ？"}' as text[]), null, 41, date '2023-10-07', cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('3d1210d6-fa47-424c-8e37-56d0fb7f08c9', 'e2524a54-3d54-4645-8a43-303d5bff59cb', 'A: Which is colder, Japan or Canada?<br/>B: Canada is colder.', 'Ａ：日本とカナダとどちらの方が寒いですか。<br/>Ｂ：カナダの方が寒いです。', 'Ａ：にほんとカナダとどちらのほうがさむいですか。<br/>Ｂ：カナダのほうがさむいです。', 'Ａ： 日[に] 本[ほん]とカナダとどちらの 方[ほう]が 寒[さむ]いですか。<br/>Ｂ：カナダの 方[ほう]が 寒[さむ]いです。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('cb16d3fd-6281-4646-ad11-0886b9ff40eb', 'e2524a54-3d54-4645-8a43-303d5bff59cb', 'A: Between you and your Japanese teacher, who is taller?<br/>I am taller (than my Japanese teacher).', 'Ａ：あなたと日本語の先生とどっちの方が背が高いですか。<br/>Ｂ：私の方が（日本語の先生より）背が高いです。', 'Ａ：あなたとにほんごのせんせいとどっちのほうがせがたかいですか。<br/>Ｂ：わたしのほうが（にほんごのせんせいより）せがたかいです。', 'Ａ：あなたと 日[に] 本[ほん] 語[ご]の 先[せん] 生[せい]とどっちの 方[ほう]が 背[せ]が 高[たか]いですか。<br/>Ｂ： 私[わたし]の 方[ほう]が（ 日[に] 本[ほん] 語[ご]の 先[せん] 生[せい]より） 背[せ]が 高[たか]いです。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('935e1ca9-98ef-4eda-a587-3f5bd921628e', 'e2524a54-3d54-4645-8a43-303d5bff59cb', 'Q: Which do you like more, summer or winter?<br/>A: I like winter more.', 'Ｑ：夏と冬、どっちが好きだ？<br/>Ａ：冬の方が好きだ。', 'Ｑ：なつとふゆ、どっちがすきだ？<br/>Ａ：ふゆのほうがすきだ。', 'Ｑ： 夏[なつ]と 冬[ふゆ]、どっちが 好[す]きだ？<br/>Ａ： 冬[ふゆ]の 方[ほう]が 好[す]きだ。', 3, cast('{"not-from-book"}' as varchar[]));
