insert into "grammar"."rule" ("id", "meaning", "title_preferred_spelling", "title_phonetic_spelling", "title_furigana_template", "how_to_use", "jlpt_level", "row_num", "date_added", "tags") values ('872eef00-764f-4dcd-89b1-1819fae14850', 'A is the most ____ (among the group of items).', 'ＡとＢとＣの中でＡが一番〇〇。<br/>Ｘの中でＡが一番〇〇。', null, null, cast('{"noun + と + noun + と + … + の中で + noun + が + 一番 + property。","category + の中で + noun + が + 一番 + property。","Note: For questions, どれ is usually used with item lists, while 何 is used with categories."}' as text[]), null, 42, date '2023-10-09', cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('0547262e-2c60-4b75-b413-fcc45c4675d2', '872eef00-764f-4dcd-89b1-1819fae14850', 'Q: Among Canada, France, and Japan, which country has the coldest climate?<br/>A: Canada is coldest, I think.', 'Ｑ：カナダとフランスと日本の中で、どこが一番寒いですか。<br/>Ａ：カナダが一番寒いと思います。', 'Ｑ：カナダとフランスとにほんのなかで、どこがいちばんさむいですか。<br/>Ａ：カナダがいちばんさむいとおもいます。', 'Ｑ：カナダとフランスと 日本[にほん]の 中[なか]で、どこが 一[いち] 番[ばん] 寒[さむ]いですか。<br/>Ａ：カナダが 一[いち] 番[ばん] 寒[さむ]いと 思[おも]います。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('8efa8208-0bb3-47f5-8a50-d7864af0cb06', '872eef00-764f-4dcd-89b1-1819fae14850', 'Q: What season do you like the best?<br/>A: I like fall the most.', 'Ｑ：季節の中でいつが一番好きてすか。<br/>Ａ：秋が一番好きです。', 'Ｑ：きせつのなかでいつがいちばんすきてすか。<br/>Ａ：あきがいちばんすきです。', 'Ｑ： 季[き] 節[せつ]の 中[なか]でいつが 一[いち] 番[ばん] 好[す]きてすか。<br/>Ａ： 秋[あき]が 一[いち] 番[ばん] 好[す]きです。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('c9628c4c-51e3-4376-932b-9d33e671e3fb', '872eef00-764f-4dcd-89b1-1819fae14850', 'Q: What Japanese food is most delicious?<br/>A: Sushi is most delicious, I think.', 'Ｑ：日本料理の中で、何が一番美味しいですか。<br/>Ａ：寿司が一番美味しいと思います。', 'Ｑ：にほんりょうりのなかで、なにがいちばんおいしいですか。<br/>Ａ：すしがいちばんおいしいとおもいます。', 'Ｑ： 日[に] 本[ほん] 料[りょう] 理[り]の 中[なか]で、 何[なに]が 一[いち] 番[ばん] 美味[おい]しいですか。<br/>Ａ： 寿[す] 司[し]が 一[いち] 番[ばん] 美味[おい]しいと 思[おも]います。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('05479085-deb5-4997-b3f6-93db65411a67', '872eef00-764f-4dcd-89b1-1819fae14850', 'Which do you like best: apples, tangerines, or cherries?', 'りんごとみかんとさくらんぼの中で、どれが一番好きですか。', 'りんごとみかんとさくらんぼのなかで、どれがいちばんすきですか。', 'りんごとみかんとさくらんぼの 中[なか]で、どれが 一[いち] 番[ばん] 好[す]きですか。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('20f70fe9-5ac2-447c-9396-c15ce404283d', '872eef00-764f-4dcd-89b1-1819fae14850', 'What fruit do you like best?', '果物の中で、何が一番好きてすか。', 'くだもののなかで、なにがいちばんすきてすか。', '果[くだ] 物[もの]の 中[なか]で、 何[なに]が 一[いち] 番[ばん] 好[す]きてすか。', 5, cast('{}' as varchar[]));
