insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('357df4b7-6870-4505-b7ba-8e3cd60b1193', 'c124fbd0-2ecb-4ce4-b4e6-f1b3534bc3c2', 'Studying Japanese has become fun<br/>(though it was like torture before).', '日本語の勉強が楽しくなりました。', 'にほんごのべんきょうがたのしくなりました。', '日[に] 本[ほん] 語[ご]の 勉[べん] 強[きょう]が 楽[たの]しくなりました。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('4f493e43-03e3-4326-9fed-5db136c123eb', 'c124fbd0-2ecb-4ce4-b4e6-f1b3534bc3c2', 'I have become fond of studying Japanese.', '日本語の勉強が好きになりました。', 'にほんごのべんきょうがいきになりました。', '日[に] 本[ほん] 語[ご]の 勉[べん] 強[きょう]が 好[い]きになりました。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('b6d63151-4c1b-452d-9b43-a50cfa2c8af1', 'c124fbd0-2ecb-4ce4-b4e6-f1b3534bc3c2', 'Mary has become better in Japanese than before.', 'メアリーさんは前より日本語が上手になりました。', 'メアリーさんはまえよりにほんごがじょうずになりました。', 'メアリーさんは 前[まえ]より 日[に] 本[ほん] 語[ご]が 上[じょう] 手[ず]になりました。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('26767e4f-0df1-49ee-a93a-53bc7110db88', 'c124fbd0-2ecb-4ce4-b4e6-f1b3534bc3c2', 'My room became clean, because I cleand it this morning.', '今朝掃除したから、私の部屋がきれいになりました。', 'けさそうじしたから、わたしのへやがきれいになりました。', '今朝[けさ] 掃[そう] 除[じ]したから、 私[わたし]の 部[へ] 屋[や]がきれいになりました。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('8991aec7-e02f-49a5-81ba-17f59aa274e9', 'c124fbd0-2ecb-4ce4-b4e6-f1b3534bc3c2', 'I have become very good at speaking Japanese, because I practiced a lot.', 'たくさん練習したから、日本語を話すのがすごく上手になりました。', 'たくさんれんしゅうしたから、にほんごをはなすのがすごくじょうずになりました。', 'たくさん 練[れん] 習[しゅう]したから、 日[に] 本[ほん] 語[ご]を 話[はな]すのがすごく 上[じょう] 手[ず]になりました。', 5, cast('{}' as varchar[]));
