insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('52148959-e4c9-48cd-81d3-720fc0e29685', '8b04fdb4-c837-4787-9a77-ccea20b77a04', 'A: Did you go anywhere?<br/>B: No, I didn''t go anywhere.', 'Ａ：どこかへ行きましたか。<br/>Ｂ：いいえ、どこへも行きませんでした。', 'Ａ：どこかへいきましたか。<br/>Ｂ：いいえ、どこへもいきませんでした。', 'Ａ：どこかへ 行[い]きましたか。<br/>Ｂ：いいえ、どこへも 行[い]きませんでした。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('c6bb90ce-191f-4320-a3d3-b5ea362ab005', '8b04fdb4-c837-4787-9a77-ccea20b77a04', 'A: Did you see anybody?<br/>B: No, I didn''t see anybody.', 'Ａ：誰かに会いましたか。<br/>Ｂ：いいえ、誰にも会いませんでした。', 'Ａ：だれかにあいましたか。<br/>Ｂ：いいえ、だれにもあいませんでした。', 'Ａ： 誰[だれ]かに 会[あ]いましたか。<br/>Ｂ：いいえ、 誰[だれ]にも 会[あ]いませんでした。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('838ea81b-3111-4b8c-875b-322c37193375', '8b04fdb4-c837-4787-9a77-ccea20b77a04', 'A: Did you do anything?<br/>B: No, I didn''t do anything.', 'Ａ：何かしましたか。<br/>Ｂ：いいえ、何もしませんでした。', 'Ａ：なにかしましたか。<br/>Ｂ：いいえ、なにもしませんでした。', 'Ａ： 何[なに]かしましたか。<br/>Ｂ：いいえ、 何[なに]もしませんでした。', 3, cast('{}' as varchar[]));
