insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('d7b7f0a6-71c1-47b0-a0eb-1ca3ee9c48e0', '669a0167-49c4-4c07-aac1-64d4877a9533', 'A: Mary,how do you get from home to North Station?<br/>B: I walk.<br/>A: I see.', 'Ａ：メアリーさんは、家から北駅までどうやって行きますか。<br/>Ｂ：歩いて行きます。Ａ：そうですか。', 'Ａ：メアリーさんは、いえからきたえきまでどうやっていきますか。<br/>Ｂ：あるいていきます。Ａ：そうですか。', 'Ａ：メアリーさんは、 家[いえ]から 北[きた] 駅[えき]までどうやって 行[い]きますか。<br/>Ｂ： 歩[ある]いて 行[い]きます。Ａ：そうですか。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('a6867125-4248-48bb-81a7-5941a5cab996', '669a0167-49c4-4c07-aac1-64d4877a9533', 'A: How do you get from North Station to the University?<br/>B: I go by bus.', 'Ａ：北駅から大学までどうやって行きますか。<br/>Ｂ：バスで行きます。', 'Ａ：きたえきからだいがくまでどうやっていきますか。<br/>Ｂ：バスでいきます。', 'Ａ： 北[きた] 駅[えき]から 大[だい] 学[がく]までどうやって 行[い]きますか。<br/>Ｂ：バスで 行[い]きます。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('7762073b-f4de-4f4f-b09a-d61d9e5c9b71', '669a0167-49c4-4c07-aac1-64d4877a9533', 'A: How long does it take to get to the University?<br/>It takes fifteen minutes.', 'Ａ：大学まで、どのぐらいかかりますか。<br/>B：十五分かかります。', 'Ａ：だいがくまで、どのぐらいかかりますか。<br/>B：じゅうごぶんかかります。', 'Ａ： 大[だい] 学[がく]まで、どのぐらいかかりますか。<br/>B： 十[じゅう] 五[ご] 分[ぶん]かかります。', 3, cast('{}' as varchar[]));
