insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('7e8b78cb-ac43-4855-b629-0313bd3df012', 'acfe362d-4c4b-4223-8fb5-c6059debc79d', 'I want to see a film this weekend.<br/>I want to go to China Someday.', '今度の週末は、映画を見たいです。<br/>いつか中国に行きたいです。', 'こんどのしゅうまつは、えいがをみたいです。<br/>いつかちゅうごくにいきたいです。', '今[こん] 度[ど]の 週[しゅう] 末[まつ]は、 映[えい] 画[が]を 見[み]たいです。<br/>いつか 中[ちゅう] 国[ごく]に 行[い]きたいです。', 1, cast('{}' as varchar[]));
