insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('3ee9b6ae-fccc-4a89-970b-b7c1fd34a088', 'acfe362d-4c4b-4223-8fb5-c6059debc79d', 'I wanted to buy a boxed lunch, therefore I went to a convenience store.', 'お弁当が買いたかったから、コンビニに行きました。', 'おべんとうがかいたかったから、コンビニにいきました。', 'お 弁[べん] 当[とう]が 買[か]いたかったから、コンビニに 行[い]きました。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('464b552c-027b-4967-b7d6-5b9f6418f5a3', 'acfe362d-4c4b-4223-8fb5-c6059debc79d', 'When I was a child, I didn''t want to play with toy trains.', '子供の時、おもちゃの電車で遊びたくなかったです。', 'こどものとき、おもちゃのでんしゃであそびたくなかったです。', '子[こ] 供[ども]の 時[とき]、おもちゃの 電[でん] 車[しゃ]で 遊[あそ]びたくなかったです。', 4, cast('{}' as varchar[]));
