insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('90ae0fed-56cf-4ac8-bbe9-bfcd82106fa3', 'bf1b4164-2102-4e58-99d1-56fc1d2d0d9c', 'In Osaka, I will do such things as shopping and eating dinner.', '大阪で買い物をしたり、晩御飯を食べたりします。', 'おおさかでかいものをしたり、ばんごはんをたべたりします。', '大[おお] 阪[さか]で 買[か]い 物[もの]をしたり、 晩[ばん] 御[ご] 飯[はん]を 食[た]べたりします。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('245905d2-459d-46e0-869c-d8cb469577a4', 'bf1b4164-2102-4e58-99d1-56fc1d2d0d9c', 'Over the weekend, I studied and talked with my friends, among other things.', '週末は、勉強したり、友達と話したりしました。', 'しゅうまつは、べんきょうしたり、ともだちとはなしたりしました。', '週[しゅう] 末[まつ]は、 勉[べん] 強[きょう]したり、 友[とも] 達[だち]と 話[はな]したりしました。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('027d4e68-c179-49d8-85b4-b130ffa4160a', 'bf1b4164-2102-4e58-99d1-56fc1d2d0d9c', 'I like dancing, listening to music, and so forth.', '踊ったり、音楽を聞いたりするのが好きです。', 'おどったり、おんがくをきいたりするのがすきです。', '踊[おど]ったり、 音[おん] 楽[がく]を 聞[き]いたりするのが 好[す]きです。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('9bf4cb64-fa39-4fbf-b916-1a9f3cb9d494', 'bf1b4164-2102-4e58-99d1-56fc1d2d0d9c', 'This weekend, I want to climb a mountain, go to a hot spring, et cetera.', '今週の週末、山に登ったり、温泉に行ったりしたいです。', 'こんしゅうのしゅうまつ、やまにのぼったり、おんせんにいったりしたいです。', '今[こん] 週[しゅう]の 週[しゅう] 末[まつ]、 山[やま]に 登[のぼ]ったり、 温[おん] 泉[せん]に 行[い]ったりしたいです。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('b5127b81-7d33-4415-8164-cda55d51875d', 'bf1b4164-2102-4e58-99d1-56fc1d2d0d9c', 'At the dormitory, you must not smoke, drink beer, et cetera.', '寮でタバコを吸ったり、ビールを飲んだりしてはいけません。', 'りょうでタバコをすったり、ビールをのんだりしてはいけません。', '寮[りょう]でタバコを 吸[す]ったり、ビールを 飲[の]んだりしてはいけません。', 5, cast('{}' as varchar[]));
