insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('051e9497-b8e8-46e8-be99-055d6e7cad71', 'd99d7c80-eb8c-4ed8-8709-3c6cc62c0fb7', 'I have had the experience of climbing Mt. Fuji.', '富士山に登ったことがあります。', 'ふじさんにのぼったことがあります。', '富[ふ] 士[じ] 山[さん]に 登[のぼ]ったことがあります。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('12892d5d-ff1c-4c30-acf4-b9ae097710f5', 'd99d7c80-eb8c-4ed8-8709-3c6cc62c0fb7', 'Takeshi has never been absent from class (in his life).', 'たけしさんは授業を休んだことがありません。', 'たけしさんはじゅぎょうをやすんだことがありません。', 'たけしさんは 授[じゅ] 業[ぎょう]を 休[やす]んだことがありません。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('defd14a0-d57c-411e-ab2e-9cef8968e6f5', 'd99d7c80-eb8c-4ed8-8709-3c6cc62c0fb7', 'A: Have you ever been to Europe?<br/>B: Yes, I have.', 'Ａ：ヨーロッパに行ったことがありますか。<br/>Ｂ：はい、あります。', 'Ａ：ヨーロッパにいったことがありますか。<br/>Ｂ：はい、あります。', 'Ａ：ヨーロッパに 行[い]ったことがありますか。<br/>Ｂ：はい、あります。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('a2d8dc4e-3122-4857-a016-bd2a88d64d51', 'd99d7c80-eb8c-4ed8-8709-3c6cc62c0fb7', 'A: Have you ever had the experience of owning a pet, such as a cat or dog?<br/>B: Yes, I have had the experience of owning a cat.', 'Ａ：猫や犬を飼ったことがありますか。<br/>Ｂ：はい、猫を飼ったことがあります。', 'Ａ：ねこやいぬをかったことがありますか。<br/>Ｂ：はい、ねこをかったことがあります。', 'Ａ： 猫[ねこ]や 犬[いぬ]を 飼[か]ったことがありますか。<br/>Ｂ：はい、 猫[ねこ]を 飼[か]ったことがあります。', 4, cast('{}' as varchar[]));
