insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('41dff3dc-6197-4bad-b8b1-d7c6d0d7f3ba', 'e756d1d7-69d1-4729-bb3c-c0d796e272b8', 'I went to Kyoto and Nara (and maybe other places as well).', '京都や奈良に行きました。', 'きょうとやならにいきました。', '京[きょう] 都[と]や 奈[な] 良[ら]に 行[い]きました。', 1, cast('{}' as varchar[]));
