insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('f7530728-7862-4548-a7d9-9f7290588971', 'e756d1d7-69d1-4729-bb3c-c0d796e272b8', 'I have had the experience of eating Tuna Sashimi and Tonkatsu Ramen, among others.', 'マグロの刺身や豚骨らーめんを食べたことがあります。', 'マグロのさしみやとんこつらーめんをたべたことがあります。', 'マグロの 刺[さし] 身[み]や 豚[とん] 骨[こつ]らーめんを 食[た]べたことがあります。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('671c6d0a-2737-4808-a71b-393a88388307', 'e756d1d7-69d1-4729-bb3c-c0d796e272b8', 'A: Among famous people, who would you like to meet?<br/>B: I''d like to meet Dr. Plait and Inori Minase.', 'Ａ：有名人の中で、誰に会いたいですか。<br/>Ｂ：フィル・プレイトや水瀬いのりに会いたいです。', 'Ａ：ゆうめいじんのなかで、だれにあいたいですか。<br/>Ｂ：フィル・プレイトやみなせいのりにあいたいです。', 'Ａ： 有[ゆう] 名[めい] 人[じん]の 中[なか]で、 誰[だれ]に 会[あ]いたいですか。<br/>Ｂ：フィル・プレイトや 水[みな] 瀬[せ]いのりに 会[あ]いたいです。', 4, cast('{}' as varchar[]));
