insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('d61594ef-d655-43c6-9afd-ee3840ead8eb', 'e0343470-cab9-4bd2-b337-3acf43e52aa0', '(As it happens,) the bus didn''t come.', 'バスが来なかったんです。', 'バスがこなかったんです。', 'バスが 来[こ]なかったんです。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('e2ae3960-0a1f-4d0e-a5b4-b3579db40f50', 'e0343470-cab9-4bd2-b337-3acf43e52aa0', 'I want to go to the bathroom. (So tell me where it is.)', 'トイレに行きたいんです。', 'トイレにいきたいんです。', 'トイレに 行[い]きたいんです。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('d8cb21e1-e048-4996-871c-5fe11c50c92d', 'e0343470-cab9-4bd2-b337-3acf43e52aa0', 'A: What''s wrong?<br/>B: My grade is not good./I got a bad grade.', 'Ａ：どうしたんですか。<br/>Ｂ：成績がよくないんです。／成績が悪かったんです。', 'Ａ：どうしたんですか。<br/>Ｂ：せいせきがよくないんです。／せいせきがわるかったんです。', 'Ａ：どうしたんですか。<br/>Ｂ： 成[せい] 績[せき]がよくないんです。／ 成[せい] 績[せき]が 悪[わる]かったんです。', 3, cast('{}' as varchar[]));
