insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('5bdc9560-0e3b-4fdd-a6e4-a0e6a45c824d', 'e0343470-cab9-4bd2-b337-3acf43e52aa0', 'The exam is over. (That''s why I''m smiling.)<br/>(In response to a person who caught you smiling.)', '試験が終わったんです。', 'しけんがおわったんです。', '試[し] 験[けん]が 終[お]わったんです。', 5, cast('{}' as varchar[]));
