insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('02d29806-d7d1-42c7-9517-020aaef31ef2', 'efddbaa9-7652-4f28-bc45-731404c43c6a', 'A: Why did you break up with your boyfriend? (You''ve got to tell me.)<br/>B: Oh, him. He never takes a bath. (That''s a good enough reason, isn''t it?)', 'Ａ：どうして彼と別れたんですか。<br/>Ｂ：彼、全然お風呂に入らないんです。', 'Ａ：どうしてかれとわかれたんですか。<br/>Ｂ：かれ、ぜんぜんおふろにはいらないんです。', 'Ａ：どうして 彼[かれ]と 別[わか]れたんですか。<br/>Ｂ： 彼[かれ]、 全[ぜん] 然[ぜん]お 風[ふ] 呂[ろ]に 入[はい]らないんです。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('98477265-f945-4823-b6d2-2f90d2681327', 'efddbaa9-7652-4f28-bc45-731404c43c6a', 'A: What happened? (You look shattered.)<br/>B: My cat dies. (That should explain how I look today).', 'Ａ：どうしたんですか。<br/>Ｂ：うちの猫が死んだんです。', 'Ａ：どうしたんですか。<br/>Ｂ：うちのねこがしんだんです。', 'Ａ：どうしたんですか。<br/>Ｂ：うちの 猫[ねこ]が 死[し]んだんです。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('cef0cd63-4c30-4577-b8ef-b22f588ed447', 'efddbaa9-7652-4f28-bc45-731404c43c6a', 'A: that''s a cute notebook.<br/>You bet. I bout it in Japan (for your information).', 'Ａ：可愛いノートですね。<br/>Ｂ：ええ。日本で買ったんです。', 'Ａ：かわいいノートですね。<br/>Ｂ：ええ。にほんでかったんです。', 'Ａ： 可愛[かわい]いノートですね。<br/>Ｂ：ええ。 日[に] 本[ほん]で 買[か]ったんです。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('f086c66c-144b-405e-bfe6-b9d3f602e682', 'efddbaa9-7652-4f28-bc45-731404c43c6a', 'A: Why are you working part time?</br>B: I don''t have money (is why).', 'Ａ：どうしてアルバイトをしているんですか。<br/>Ｂ：お金がないんです。', 'Ａ：どうしてアルバイトをしているんですか。<br/>Ｂ：おかねがないんです。', 'Ａ：どうしてアルバイトをしているんですか。<br/>Ｂ：お 金[かね]がないんです。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('cf05ac5d-3250-474e-9950-18c094b50aa5', 'efddbaa9-7652-4f28-bc45-731404c43c6a', 'A: Why are you nervous?<br/>B: I have a test. (That is why.)', 'Ａ：どうして緊張しているんですか。<br/>Ｂ：テストがあるんです。', 'Ａ：どうしてきんちょうしているんですか。<br/>Ｂ：テストがあるんです。', 'Ａ：どうして 緊[きん] 張[ちょう]しているんですか。<br/>Ｂ：テストがあるんです。', 5, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('a118fd93-2baf-4f94-b263-14da33f6a1c9', 'efddbaa9-7652-4f28-bc45-731404c43c6a', 'A: Why did you skip class yesterday?<br/>B: I was sleepy (is why).', 'Ａ：どうして昨日授業をサボったんですか。<br/>Ｂ：眠かったんです。', 'Ａ：どうしてきのうじゅぎょうをサボったんですか。<br/>Ｂ：ねむかったんです。', 'Ａ：どうして 昨日[きのう] 授[じゅ] 業[ぎょう]をサボったんですか。<br/>Ｂ： 眠[ねむ]かったんです。', 6, cast('{}' as varchar[]));
