insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('9f327666-e8d2-4807-aed6-19af4cf32990', 'e007254a-59f0-4ec3-8a0b-41fbd0684148', 'This town is too quiet.', 'この町は静かすぎます。', 'このまちはしずかすぎます。', 'この 町[まち]は 静[しず]かすぎます。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('be4c9c52-586d-46dc-ae32-3934d916f862', 'e007254a-59f0-4ec3-8a0b-41fbd0684148', 'I got up too early.<br/>My Mom talks too much.<br/>You must not eat too much.', '早く起きすぎました。<br/>母は話しすぎます。<br/>食べすぎてはいけません。', 'はやくおきすぎました。<br/>ははははしすぎます。<br/>たべすぎてはいけません。', '早[はや]く 起[お]きすぎました。<br/> 母[はは]は 話[は]しすぎます。<br/> 食[た]べすぎてはいけません。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('d8369036-71ea-4092-910e-f00c38e78ef2', 'e007254a-59f0-4ec3-8a0b-41fbd0684148', 'Today it''s too cold, therefore I don''t want to go to school.', '今日は寒すぎるから、学校に行きたくないです。', 'きょうはさむすぎるから、がっこうにいきたくないです。', '今日[きょう]は 寒[さむ]すぎるから、 学[がっ] 校[こう]に 行[い]きたくないです。', 4, cast('{}' as varchar[]));
