insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('70519e21-59e4-474d-8fa1-972c2bfbc7ba', '42986145-a13f-49b5-904a-e6f610ddb20f', 'I did not sleep last night because I had a lot of homework.<br/>(I had a lot of homework, therefore I did not sleep last night.)', '宿題がたくさんあったので、昨日の夜、寝ませんでした。', 'しゅくだいがたくさんあったので、きのうのよる、ねませんでした。', '宿[しゅく] 題[だい]がたくさんあったので、 昨日[きのう]の 夜[よる]、 寝[ね]ませんでした。', 2, cast('{}' as varchar[]));
