insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('5104ac1d-b806-4aaf-aab6-c9f1370398fb' as uuid), cast('c1cb4718-94af-4017-9671-4561b9738aae' as uuid), 'I like studying the Japanese language.', '日本語を勉強するのが好きです。', 'にほんごをべんきょうするのがすきです。', '日[に] 本[ほん] 語[ご]を 勉[べん] 強[きょう]するのが 好[す]きです。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('728ba088-711f-4479-9fb5-7ee966dd4894' as uuid), cast('c1cb4718-94af-4017-9671-4561b9738aae' as uuid), 'I don''t like cleaning my room.', '部屋を掃除するのが嫌いです', 'へやをそうじするのがきらいです', '部[へ] 屋[や]を 掃[そう] 除[じ]するのが 嫌[きら]いです', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('4543a970-3341-4614-bac4-c72da11242b4' as uuid), cast('c1cb4718-94af-4017-9671-4561b9738aae' as uuid), 'Robert is good at cooking meals.', 'ロバートさんは料理を作るのが上手です。', 'ロバートさんはりょうりをつくるのがじょうずです。', 'ロバートさんは 料[りょう] 理[り]を 作[つく]るのが 上[じょう] 手[ず]です。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('a424a513-0f2b-4a27-bf54-9e88c01f321a' as uuid), cast('c1cb4718-94af-4017-9671-4561b9738aae' as uuid), 'Takeshi is not a good speaker of English.', 'たけしさんは英語を話すのが下手です。', 'たけしさんはえいごをはなすのがへたです。', 'たけしさんは 英[えい] 語[ご]を 話[はな]すのが 下手[へた]です。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('fa1506ed-4868-4b75-bda6-1341df85c822' as uuid), cast('c1cb4718-94af-4017-9671-4561b9738aae' as uuid), 'Q: What does the Japanese teacher like to do?<br/>I think they like driving a car.', 'Ｑ：日本語の先生は何をするのが好きですか。<br/>Ａ：車を運転するのが好きだと思います。', 'Ｑ：にほんごのせんせいはなにをするのがすきですか。<br/>Ａ：くるまをうんてんするのがすきだとおもいます。', 'Ｑ： 日[に] 本[ほん] 語[ご]の 先[せん] 生[せい]は 何[なに]をするのが 好[す]きですか。<br/>Ａ： 車[くるま]を 運[うん] 転[てん]するのが 好[す]きだと 思[おも]います。', 5, cast('{}' as varchar[]));
