insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('3d1210d6-fa47-424c-8e37-56d0fb7f08c9' as uuid), cast('e2524a54-3d54-4645-8a43-303d5bff59cb' as uuid), 'A: Which is colder, Japan or Canada?<br/>B: Canada is colder.', 'Ａ：日本とカナダとどちらの方が寒いですか。<br/>Ｂ：カナダの方が寒いです。', 'Ａ：にほんとカナダとどちらのほうがさむいですか。<br/>Ｂ：カナダのほうがさむいです。', 'Ａ： 日[に] 本[ほん]とカナダとどちらの 方[ほう]が 寒[さむ]いですか。<br/>Ｂ：カナダの 方[ほう]が 寒[さむ]いです。', 1, cast('{}' as varchar[]));
