insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('a6867125-4248-48bb-81a7-5941a5cab996' as uuid), cast('669a0167-49c4-4c07-aac1-64d4877a9533' as uuid), 'A: How do you get from North Station to the University?<br/>B: I go by bus.', 'Ａ：北駅から大学までどうやって行きますか。<br/>Ｂ：バスで行きます。', 'Ａ：きたえきからだいがくまでどうやっていきますか。<br/>Ｂ：バスでいきます。', 'Ａ： 北[きた] 駅[えき]から 大[だい] 学[がく]までどうやって 行[い]きますか。<br/>Ｂ：バスで 行[い]きます。', 2, cast('{}' as varchar[]));
