insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('b34e0315-3a15-4998-94f3-a30a04c1a7cf' as uuid), cast('dedb9dde-3d23-4004-adce-6d90a3c7915e' as uuid), 'A: That karate teacher is really good at teaching.<br/>B: Well then, I will try going to their karate class.', 'Ａ：あの空手の先生は教えるのがとても上手ですよ。<br/>B：じゃあ、空手のクラスに行ってみます。', 'Ａ：あのからてのせんせいはおしえるのがとてもじょうずですよ。<br/>B：じゃあ、からてのクラスにいってみます。', 'Ａ：あの 空[から] 手[て]の 先[せん] 生[せい]は 教[おし]えるのがとても 上手[じょうず]ですよ。<br/>B：じゃあ、 空[から] 手[て]のクラスに 行[い]ってみます。', 3, cast('{}' as varchar[]));
