insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('19571f53-e235-49fa-845b-5311189002f3' as uuid), cast('dedb9dde-3d23-4004-adce-6d90a3c7915e' as uuid), 'A: Korean food is spicy and very delicious!<br/>B: Okay, I will try eating it.', 'Ａ：韓国の料理は辛くて、とても美味しですよ。<br/>Ｂ：じゃあ、食べてみます。', 'Ａ：かんこくのりょうりはからくて、とてもおいしですよ。<br/>Ｂ：じゃあ、たべてみます。', 'Ａ： 韓[かん] 国[こく]の 料[りょう] 理[り]は 辛[から]くて、とても 美味[おい]しですよ。<br/>Ｂ：じゃあ、 食[た]べてみます。', 6, cast('{}' as varchar[]));
