insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('7080910a-7ccb-4cb5-99e5-8cf40e6e20da' as uuid), cast('f132b1f1-1660-4206-8eec-164438e9f3d1' as uuid), 'I want a good grade.', 'いい成績が欲しいです。', 'いいせいせきがほしいです。', 'いい 成績[せいせき]が 欲[ほ]しいです。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('a69c7cdb-f1a1-48d4-b92b-6f33bc90e258' as uuid), cast('f132b1f1-1660-4206-8eec-164438e9f3d1' as uuid), 'When I was young, I wanted a Pokémon game.', '子供の時、ポケモンのゲームが欲しかったです。', 'こどものとき、ポケモンのゲームがほしかったです。', '子[こ] 供[ども]の 時[とき]、ポケモンのゲームが 欲[ほ]しかったです。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('b8f6faad-2f49-4c4d-b235-d6286edac9d3' as uuid), cast('f132b1f1-1660-4206-8eec-164438e9f3d1' as uuid), 'A: Do you want a present?<br/>B: No, I don''t (want).', 'Ａ：プレゼントが欲しい？<br/>Ｂ：ううん、欲しくない。', 'Ａ：プレゼントがほしい？<br/>Ｂ：ううん、ほしくない。', 'Ａ：プレゼントが 欲[ほ]しい？<br/>Ｂ：ううん、 欲[ほ]しくない。', 3, cast('{}' as varchar[]));
