insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('b622d86d-a1c1-457e-8879-84fba8caf0ea' as uuid), cast('f132b1f1-1660-4206-8eec-164438e9f3d1' as uuid), 'A: This upcoming birthday, what do you want? Why?<br/>I want a new camera because the current one is old and big.', 'Ａ：今度の誕生日に何が欲しいですか。とうしてですか。<br/>新しいカメラが欲しいです。今のは古くて大きいですから。', 'Ａ：こんどのたんじょうびになにがほしいですか。とうしてですか。<br/>あたらしいカメラがほしいです。いまのはふるくておおきいですから。', 'Ａ： 今[こん] 度[ど]の 誕[たん] 生[じょう] 日[び]に 何[なに]が 欲[ほ]しいですか。とうしてですか。<br/> 新[あたら]しいカメラが 欲[ほ]しいです。 今[いま]のは 古[ふる]くて 大[おお]きいですから。', 5, cast('{}' as varchar[]));
