insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('7706b218-5e2b-470e-915a-39ab0238dc92' as uuid), cast('255b5117-c1d3-4726-85cd-82f934e6d4e9' as uuid), 'A:You got ¥100,000 from your parents? What are you using it for?<br/>B: I am going on a trip. (decision already made)', 'Ａ：両親から十万円もらったんですか。何に使うんですか。<br/>Ｂ：旅行に行こうと思っています。', 'Ａ：りょうしんからじゅうまんえんもらったんですか。なににつかうんですか。<br/>Ｂ：りょこうにいこうとおもっています。', 'Ａ： 両[りょう] 親[しん]から 十[じゅう] 万[まん] 円[えん]もらったんですか。 何[なに]に 使[つか]うんですか。<br/>Ｂ： 旅[りょ] 行[こう]に 行[い]こうと 思[おも]っています。', 2, cast('{}' as varchar[]));
