insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('7c30bb92-dd8f-409a-b336-300a82428fe6' as uuid), cast('255b5117-c1d3-4726-85cd-82f934e6d4e9' as uuid), 'I always eat too many sweets, so this year I''ve decided to eat more vegetables. And then, about 3 times a week, I''ve decided to exercise.', 'いつもお菓子を食べすぎるので、今年はもっと野菜を食べようと思っています。それから、１週間に３回ぐらい運動しようと思っています。', 'いつもおかしをたべすぎるので、ことしはもっとやさいをたべようとおもっています。それから、いっしゅうかんにさんかいぐらいうんどうしようとおもっています。', 'いつもお 菓[か] 子[し]を 食[た]べすぎるので、 今[こ] 年[とし]はもっと 野[や] 菜[さい]を 食[た]べようと 思[おも]っています。それから、 １[いっ] 週[しゅう] 間[かん]に ３[さん] 回[かい]ぐらい 運[うん] 動[どう]しようと 思[おも]っています。', 6, cast('{}' as varchar[]));
