insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('ac79566b-d425-49bd-bd44-0b450ee2806b' as uuid), cast('97234b9f-a79b-4214-a9f4-4ebe57e019b9' as uuid), 'I will give you 100,000 yen. What will you use it for?<br/>I will go on a trip. (decision made on the spot)', 'Ａ：十万円あげましょう。何に使いますか。<br/>Ｂ：旅行に行こうと思います。', 'Ａ：じゅうまんえんあげましょう。なににつかいますか。<br/>Ｂ：りょこうにいこうとおもいます。', 'Ａ： 十[じゅう] 万[まん] 円[えん]あげましょう。 何[なに]に 使[つか]いますか。<br/>Ｂ： 旅[りょ] 行[こう]に 行[い]こうと 思[おも]います。', 1, cast('{}' as varchar[]));
