insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('802befcf-55c8-4fff-8103-f05c93d1003c' as uuid), cast('ef2282a7-7e4d-421a-92eb-8c1f82b5ec0b' as uuid), 'Soon you will be going on a trip to a foreign country. What must you do to prepare?', 'もうすぐ海外旅行に行きます。何をしておかなければいけませんか。', 'もうすぐかいがいりょこうにいきます。なにをしておかなければいけませんか。', 'もうすぐ 海[かい] 外[がい] 旅[りょ] 行[こう]に 行[い]きます。 何[なに]をしておかなければいけませんか。', 6, cast('{}' as varchar[]));
