insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('5ce24f61-e479-4cb9-bce0-4874b2184a26' as uuid), cast('9c7bf747-f9ef-4046-9f58-eee10d77c8b8' as uuid), 'Friend: Next year I plan to study abroad.<br/>You: I hope you get to have various experiences.', '友達：来年留学する予定です。<br/>あなた：色々な経験ができるといいですね。', 'ともだち：らいねんりゅうがくするよていです。<br/>あなた：いろいろなけいけんができるといいですね。', '友[とも] 達[だち]： 来[らい] 年[ねん] 留[りゅう] 学[がく]する 予[よ] 定[てい]です。<br/>あなた： 色[いろ] 々[いろ]な 経[けい] 験[けん]ができるといいですね。', 3, cast('{}' as varchar[]));
