insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('0cfea0f1-f3c6-411d-9855-b00f29fec7f5' as uuid), cast('fb2f3737-38e2-4d89-9d8e-49f4d83bb445' as uuid), 'I (completely) finished reading the book.', '本を読んでしまいました。', 'ほんをよんでしまいました。', '本[ほん]を 読[よ]んでしまいました。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('c11b0708-1de6-4a51-8177-97d01ebb7549' as uuid), cast('fb2f3737-38e2-4d89-9d8e-49f4d83bb445' as uuid), 'I inadvertently left my bag on the train.', '電車の中に鞄を忘れてしまいました。', 'でんしゃのなかにかばんをわすれてしまいました。', '電[でん] 車[しゃ]の 中[なか]に 鞄[かばん]を 忘[わす]れてしまいました。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('05bde5e7-bee9-4f86-a4b2-1cb46cac6d43' as uuid), cast('fb2f3737-38e2-4d89-9d8e-49f4d83bb445' as uuid), 'I already finished writing a paper.', 'もうレポートを書いてしまいました。', 'もうレポートをかいてしまいました。', 'もうレポートを 書[か]いてしまいました。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('70ff1d76-9e88-4052-9588-1fa8a2b7443c' as uuid), cast('fb2f3737-38e2-4d89-9d8e-49f4d83bb445' as uuid), 'Since my friend didn''t keep her promise, I (regrettably) had a fight.', '友達が約束を守らなかったので、喧嘩してしまいました。', 'ともだちがやくそくをまもらなかったので、けんかしてしまいました。', '友[とも] 達[だち]が 約[やく] 束[そく]を 守[まも]らなかったので、 喧[けん] 嘩[か]してしまいました。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('8c21615b-8d2d-42ab-9556-15c0a1aecc4c' as uuid), cast('fb2f3737-38e2-4d89-9d8e-49f4d83bb445' as uuid), 'I broke my father''s car, but I (regrettably) broke it.', '父の車を借りましたが、壊してしまいました。', 'ちちのくるまをかりましたが、こわしてしまいました。', '父[ちち]の 車[くるま]を 借[か]りましたが、 壊[こわ]してしまいました。', 5, cast('{}' as varchar[]));
