insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('5d3608e5-f82c-4dc7-84f3-55d2e2568217' as uuid), cast('ebf4802e-1eae-4403-bd51-46103b79f1ee' as uuid), 'I (regrettably) lost my homework!', '宿題をなくしちゃった。', 'しゅくだいをなくしちゃった。', '宿[しゅく] 題[だい]をなくしちゃった。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('b11cdf03-fa5c-4ff8-8f76-847da30ce038' as uuid), cast('ebf4802e-1eae-4403-bd51-46103b79f1ee' as uuid), 'A: There''s no milk in the fridge...<br/>B: Sorry. I drank it, completely finishing it off. <i>OR</i> Sorry. I regrettably drank it.', 'Ａ：冷蔵庫の牛乳がないんけど……。<br/>Ｂ：ごめん。飲んじゃった。', 'Ａ：れいぞうこのぎゅうにゅうがないんけど……。<br/>Ｂ：ごめん。のんじゃった。', 'Ａ： 冷[れい] 蔵[ぞう] 庫[こ]の 牛[ぎゅう] 乳[にゅう]がないんけど……。<br/>Ｂ：ごめん。 飲[の]んじゃった。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('daa7a7f5-cd81-4d43-8c2b-6a974338f6a6' as uuid), cast('ebf4802e-1eae-4403-bd51-46103b79f1ee' as uuid), 'A: The magazine on the desk is also gone.<br/>B: To tell you the truth, I (regrettably) threw them away.', 'Ａ：机の上の雑誌もない。<br/>Ｂ：ごめん。実は捨てちゃった。', 'Ａ：つくえのうえのざっしもない。<br/>Ｂ：ごめん。じつはすてちゃった。', 'Ａ： 机[つくえ]の 上[うえ]の 雑[ざっ] 誌[し]もない。<br/>Ｂ：ごめん。 実[じつ]は 捨[す]てちゃった。', 3, cast('{}' as varchar[]));
