insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('4af1259c-9296-48bb-a97a-df9ee186e5cc' as uuid), cast('b597e03b-4402-40e9-b2a6-62aa21253df7' as uuid), 'Whenever I talk with that person, I feel uplifted.', '私はその人と話すと元気になる。', 'わたしはそのひととはなすとげんきになる。', '私[わたし]はその 人[ひと]と 話[はな]すと 元[げん] 気[き]になる。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('e19f8f88-c648-48ec-936d-251cbc5e7408' as uuid), cast('b597e03b-4402-40e9-b2a6-62aa21253df7' as uuid), 'Whenever the streets are crowded, it takes longer to get there.', '道が混んでいると時間がかかる。', 'みちがこんでいるとじかんがかかる。', '道[みち]が 混[こ]んでいると 時[じ] 間[かん]がかかる。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('a4f162b3-bc81-4a32-ba12-5334d3c29a50' as uuid), cast('b597e03b-4402-40e9-b2a6-62aa21253df7' as uuid), 'When I was young, whenever winter arrived, I caught a cold.', '私は子供の時、冬になると風邪を引きました', 'わたしはこどものとき、ふゆになるとかぜをひきました', '私[わたし]は 子[こ] 供[ども]の 時[とき]、 冬[ふゆ]になると 風[か] 邪[ぜ]を 引[ひ]きました', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('2851d11d-bdc2-4823-9f04-eba3ae13f5e1' as uuid), cast('b597e03b-4402-40e9-b2a6-62aa21253df7' as uuid), 'Whenever I get nervous, I get thirsty.', '緊張すると、喉が渇きます。', 'きんちょうすると、のどがかわきます。', '緊[きん] 張[ちょう]すると、 喉[のど]が 渇[かわ]きます。', 4, cast('{}' as varchar[]));
