insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('9060b52a-32be-4857-b5eb-98cf12676f15' as uuid), cast('a58249a4-06c3-45ce-b026-33a73eecbbf5' as uuid), 'If Mary goes back home, we will become sad/lonely.', 'メアリーさんが国に帰ると寂しくなります。', 'メアリーさんがくににかえるとさびしくなります。', 'メアリーさんが 国[くに]に 帰[かえ]ると 寂[さび]しくなります。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('cf977382-568b-42df-bafa-f31326d79893' as uuid), cast('a58249a4-06c3-45ce-b026-33a73eecbbf5' as uuid), 'Whenever fall arrives, trees turn red.', '秋になると木が赤くなります。', 'あきになるときがあかくなります。', '秋[あき]になると 木[き]が 赤[あか]くなります。', 2, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('f207ae3a-4dbe-480c-b275-9b4bdefd21f1' as uuid), cast('a58249a4-06c3-45ce-b026-33a73eecbbf5' as uuid), 'Whenever night comes, the town becomes quiet.', '夜になると町が静かになります。', 'よるになるとまちがしずかになります。', '夜[よる]になると 町[まち]が 静[しず]かになります。', 3, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('225dae80-f417-4572-aecc-d5133675781a' as uuid), cast('a58249a4-06c3-45ce-b026-33a73eecbbf5' as uuid), 'Whenever a response doesn''t come from a friend, I get uneasy.', '友達から返事が来ないと、不安になります。', 'ともだちからへんじがこないと、ふあんになります。', '友達[ともだち]から 返事[へんじ]が 来[こ]ないと、 不[ふ] 安[あん]になります。', 4, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('7b53d4f2-b614-4e07-8742-db01a73f8b01' as uuid), cast('a58249a4-06c3-45ce-b026-33a73eecbbf5' as uuid), 'When spring comes, cherry blossoms bloom.', '春が来ると、桜が咲きます。', 'はるがくるると、さくらがさきます。', '春[はる]が 来[くる]ると、 桜[さくら]が 咲[さ]きます。', 5, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('0423ed89-6d89-48b8-876c-8724e251070a' as uuid), cast('a58249a4-06c3-45ce-b026-33a73eecbbf5' as uuid), 'Whenever you fall in a crowded place, it''s embarrassing.', '人がたくさんいる所で転ぶと、恥ずかしくなります。', 'ひとがたくさんいるところでころぶと、はずかしくなります。', '人[ひと]がたくさんいる 所[ところ]で 転[ころ]ぶと、 恥[は]ずかしくなります。', 6, cast('{}' as varchar[]));
