insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('be4851ff-9d34-474a-91b4-3fd2e11009a6' as uuid), cast('fdeb200b-f829-4b5c-b696-c9bcbc565c62' as uuid), 'I always listen to music while studying Japanese.', '私はいつも音楽を聞きながら日本語を勉強します。', 'わたしはいつもおんがくをききながらにほんごをべんきょうします。', '私[わたし]はいつも 音[おん] 楽[がく]を 聞[き]きながら 日[に] 本[ほん] 語[ご]を 勉[べん] 強[きょう]します。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('2fe14594-3cc0-4993-a11a-d65a696e0c5c' as uuid), cast('fdeb200b-f829-4b5c-b696-c9bcbc565c62' as uuid), 'Fumino Furuhashi is singing a song while doing laundry.', '古橋文乃さんは歌を歌いながら洗濯しています。', 'ふるはしふみのさんはうたをうたいながらせんたくしています。', '古[ふる] 橋[はし] 文[ふみ] 乃[の]さんは 歌[うた]を 歌[うた]いながら 洗濯[せんたく]しています。', 2, cast('{}' as varchar[]));
