insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('ccdf847d-a91b-448d-99ed-0581925ff407' as uuid), cast('f8a7d609-b489-4554-9ce4-03efb9320cc6' as uuid), 'I wish I had told her that I loved her.', 'あの時、「愛している」と言えばよかったです。', 'あのとき、「あいしている」といえばよかったです。', 'あの 時[とき]、「 愛[あい]している」と 言[い]えばよかったです。', 1, cast('{}' as varchar[]));
