insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values (cast('a7684f46-5397-4c89-8c80-ac4ba9db3138' as uuid), cast('f8a7d609-b489-4554-9ce4-03efb9320cc6' as uuid), 'I bought a refrigerator, however right away it completely broke. I should not have bought a cheap one.', '冷蔵庫を買いましたが、すぐ壊れてしまいました。安いのを買わなければよかったです。', 'れいぞうこをかいましたが、すぐこわれてしまいました。やいのをかわなければよかったです。', '冷[れい] 蔵[ぞう] 庫[こ]を 買[か]いましたが、すぐ 壊[こわ]れてしまいました。 安[や]いのを 買[か]わなければよかったです。', 4, cast('{}' as varchar[]));
