insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('80d6261c-89df-4f07-9b36-5ab262066b30', 'f7851ce8-e9b1-47c4-82bc-47626526312a', 'The cat has brought something.', '猫が何か持ってきました。', 'ねこがなにかもってきました。', '猫[ねこ]が 何[なに]か 持[も]ってきました。', 1, cast('{}' as varchar[]));
insert into "grammar"."example" ("id", "rule_id", "meaning", "text_preferred_spelling", "text_phonetic_spelling", "text_furigana_template", "sort_rank", "tags") values ('a46eeb59-60d1-4d27-8602-4910f2c2d38b', 'f7851ce8-e9b1-47c4-82bc-47626526312a', 'Q: Did the cat eat anything?<br/>A: No, the cat did not eat anything.', 'Ｑ：猫は何か食べましたか。<br/>Ａ：いいえ、猫は何も食べませんでした。', 'Ｑ：ねこはなにかたべましたか。<br/>Ａ：いいえ、ねこはなにもたべませんでした。', 'Ｑ： 猫[ねこ]は 何[なに]か 食[た]べましたか。<br/>Ａ：いいえ、 猫[ねこ]は 何[なに]も 食[た]べませんでした。', 2, cast('{}' as varchar[]));
